mapper.select:


     and t.family_id = #{familyId}
     and t.amount = #{amount}
     and t.txn_type = #{txnType}
     and t.category = #{category}
     and t.id = #{id}
     and date(t.transaction_date) = date(#{transactionDate})
     and t.note like concat('%', #{note}, '%')
     and fm.name = #{memberName}

order by t.transaction_date desc, t.flow_id desc

mapper.update:
update transactions
family_id = #{familyId}, amount = #{amount}, txn_type = #{txnType}, category = #{category}, id = #{id}, transaction_date = #{transactionDate}, note = #{note},
where flow_id = #{flowId}
